import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {Table, Row, Column, Cell} from 'fixed-data-table';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://gist.githubusercontent.com/tdreyno/4278655/raw/7b0762c09b519f40397e4c3e100b097d861f5588/airports.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <Table pagination={{ pageSizeOptions: ['25', '50', '100', '200'] }}
        height={40}
        >
          {items.map(item => (
            <Row key={item.code}>
              <Column>{item.code}</Column>
              <Column>{item.name}</Column>
              <Column>{item.city}</Column>
            </Row>
          ))}
        </Table>
        /** Displays only table, no pagination, no sorting **/
        // <table border="1">
        //   <thead>
        //     <td>Code</td>
        //     <td>Name</td>
        //     <td>City</td>
        //   </thead>
        //   <tbody>
        //   {items.map(item => (
        //     <tr key={item.code}>
        //       <td>{item.code}</td>
        //       <td>{item.name}</td>
        //       <td>{item.city}</td>
        //     </tr>
        //   ))}
        //   </tbody>
        // </table>
      );
    }
  }
}

export default App;
