import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://gist.githubusercontent.com/tdreyno/4278655/raw/7b0762c09b519f40397e4c3e100b097d861f5588/airports.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  
  //Pagination
  changeToPage(table, page, rowsPerPage) {

    let startItem = (page - 1) * rowsPerPage;
    let endItem = startItem + rowsPerPage;
    let navAs = table.querySelectorAll('.nav a');
    let tBodyRows = table.querySelectorAll('tBody tr');

    for (let nix = 0; nix < navAs.length; nix++) {

        if (nix == page - 1)
            navAs[nix].classList.add('active');
        else 
            navAs[nix].classList.remove('active');

        for (let trix = 0; trix < tBodyRows.length; trix++) 
            tBodyRows[trix].style.display = 
                (trix >= startItem && trix < endItem)
                ? 'table-row'
                : 'none';  

    }

}
  addPagerToTable(table, rowsPerPage = 10) {

    let tBodyRows = table.querySelectorAll('tBody tr');
    let numPages = Math.ceil(tBodyRows.length/rowsPerPage);

    let colCount = 
    [].slice.call(
        table.querySelector('tr').cells
    )
    .reduce((a,b) => a + parseInt(b.colSpan), 0);

    table
    .createTFoot()
    .insertRow()
    .innerHTML = `<td colspan=${colCount}><div class="nav"></div></td>`;

    if(numPages == 1)
        return;

    for(i = 0;i < numPages;i++) {

        let pageNum = i + 1;

        table.querySelector('.nav')
        .insertAdjacentHTML(
            'beforeend',
            `<a href="#" rel="${i}">${pageNum}</a> `        
        );

    }

    changeToPage(table, 1, rowsPerPage);

    for (let navA of table.querySelectorAll('.nav a'))
        navA.addEventListener(
            'click', 
            e => changeToPage(
                table, 
                parseInt(e.target.innerHTML), 
                rowsPerPage
            )
        );

}
  addPagerToTables(tables, rowsPerPage = 10) {

      tables = 
          typeof tables == "string"
        ? document.querySelectorAll(tables)
        : tables;

      for (let table of tables) 
          addPagerToTable(table, rowsPerPage);

  }


  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div align='center'>Error: {error.message}</div>;
    } 
    else if (!isLoaded) {
      return <div align="center">Loading...</div>;
    } 
    else { var id=0
      addPagerToTables('#tb_res', 50);

      return (
        <div align="center">
          <h2>JSON into Table</h2>
          <table align="center" cellPadding='6' cellSpacing='0' id="tb_res">
            <thead> 
              <td>Sl. No. &nbsp;</td>
              <td>Code</td>
              <td>Name</td>
              <td>City</td>
              <td>Country</td>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.code}>
                  <td>{++id}</td>
                  <td>{item.code}</td>
                  <td>{item.name?item.name:"Aditya Pranav Bhuvanapalli"}</td>
                  <td>{item.city}</td>
                  <td>{item.country}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }
}

export default App;
